INSERT INTO `noticias` (`NoticiasPK`, `Fecha`, `Mes`, `Anio`, `Titulo`, `Texto`, `Enlace`, `EnlaceVideo`, `TextoVideo`) VALUES
(1, '2013-03-01', 'Marzo', '2013', '', 'Actualmente se est&aacute;n digitalizando los ejemplares de bibliotecas de Avila, León, Segovia, Valladolid y Zamora.', '', '', ''),
(2, '2013-03-07', 'Marzo ', '2013', 'The Afterlife of Ovid( Londres, 7 - 8 March 2013).', 'Coloquio sobre la pervivencia de Ovidio en Instituto Warburg de Londres.', 'http://warburg.sas.ac.uk/events/colloquia/afterlife-of-ovid/', 'http://www.youtube.com/watch?v=LxQYpu2-Piw', 'Enlace a la conferencia sobre las ediciones ilustradas de Ovidio en el siglo XVI (Fátima Díez Platas)'),
(3, '2013-06-01', 'Junio', '2013', 'HISTORIA DEL ARTE DIGITAL<br/>               III Workshop internacional Málaga 20-22 de junio.', '', 'http://historiadelartemalaga.es/seminarios/workshop/', '', ''),
(4, '2013-07-09', 'Julio', '2013', '«Humanidades Digitales: desafíos, logros y perspectivas de futuro», I Congreso Internacional de la HDH: Humanidades Digitales Hispánicas. Sociedad Internacional. ', 'A Coruña, 9-12 de julio', 'http://hdh2013.humanidadesdigitales.org', '', ''),
(5, '2014-02-28', 'Febrero', '2014', '', 'Actualmente se está en proceso de incorporación de los ejemplares encontrados en:\r\n<ul><li>Biblioteca Histórica de Santa Cruz de la Universidad de Valladolid</li>\r\n<li>Biblioteca del Estudio Teológico Agustiniano de Valladolid</li>\r\n<li>Biblioteca General de la Universidad de Salamanca<</li>\r\n<li>Biblioteca Pública de Segovia</li>\r\n', '', '', ''),
(6, '2014-09-24', 'Septiembre', '2014', '\r\n5th Global Conference\r\nDigital Memories\r\nWednesday 24th September – Friday 26th September 2014 Mansfield College, Oxford, United Kingdom\r\n', '\"A Virtual Museum-Library: The Case of the Ovidian Digital Library (Biblioteca Digital Ovidiana)\"<br/>\r\nFátima Díez-Platas, Paloma Centenera and María Luisa Díez-Platas', 'http://www.inter-disciplinary.net/critical-issues/cyber/digital-memories/', '', ''),
(7, '2015-05-01', 'Mayo', '2015', '', '<div style=\"margin-left:-20px !important\">Se ha concluido la tercera fase del proyecto <strong>BIBLIOTECA DIGITAL OVIDIANA (2012 - 2014): “Biblioteca digital ovidiana: ediciones ilustradas de Ovidio, siglos XV-XIX (III): las bibliotecas de Castilla y León” (HAR2011-25853)</strong>.<br/>\r\nEn esta tercera fase se han visitado 138 bibliotecas y se han localizado, consultado y digitalizado 129 ejemplares de 65 ediciones.<br/>\r\nEn este momento se están terminando de incorporar a la base de datos los ejemplares estudiados y se está estudiando de manera especial la ilustración de Ovidio en el siglo XVIII, que se refleja en las últimas ediciones alojadas en el sitio Web.</div>\r\n', '', '', ''),
(8, '2015-06-22', 'Junio', '2015', 'Picasso. Las Metamorfosis de Ovidio y el libro ilustrado. Cinco siglos de relatos a través del grabado', 'Una serie de ediciones ilustradas de Ovidio de la BNE acompañarán a los aguafuertes de Picasso realizados para la edición de las Metamorfosis publicada por Skira en 1930 en una exposición en la Casa Natal de Picasso de Málaga.', 'http://www.laopiniondemalaga.es/cultura-espectaculos/2015/06/22/andalucia-malaga-cultura-biblioteca-nacional/775730.html ', '', ''),
(9, '2015-10-30', 'Octubre', '2015', 'Biblioteca Histórica de la UCM Marqués de Valdecilla.', 'En el marco de la nueva fase del proyecto BIBLIOTECA DIGITAL OVIDIANA, que se ocupa de las bibliotecas de la Comunidad de Madrid, durante el mes de octubre se ha iniciado y completado la consulta y digitalización de los ejemplares del extraordinario fondo de  la Biblioteca Histórica de la Universidad Complutense de Madrid, Marques de Valdecilla.', ' http://biblioteca.ucm.es/blogs/Foliocomplutense/ ', '', '');
INSERT INTO `obra` (`ObraPK`, `ObraLat`, `Obra`, `Descripcion`, `viaf`, `handler`) VALUES
(1, 'Metamorphoseis', 'Metamorfosis', 'Poema épico en hexámetros dactílicos dividido en quince libros. En ellos se entrelazan las referencias contemporáneas al tiempo del poeta con las historia mitológicas de la Antigüedad, conducidas por el motivo recurrente del cambio de forma. En las Metamorfosis se narra otra historia del mundo, desde los inicios del universo y la separación de los elementos hasta la muerte de Julio César y su transformación en estrella', '301102439', NULL),
(2, 'Epistulae Heroidum', 'Heroidas', 'En esta obra Ovidio decide prestarles voz a quince mujeres atribuladas por el amor. La obra se compone de quince cartas simples, de las heroínas a sus esposos o amantes y seis cartas dobles, que incluyen las cartas de cortejo de tres amantes a sus amadas y las respuestas de estas', '302466394', NULL),
(3, 'Fasti', 'Fastos', 'Compuesto por seis libros, cada uno de ellos dedicado a los seis primeros meses del año, en los que se relatan las fiestas, las ceremonias y los aniversarios más significativos', '990145856882922920263', NULL),
(4, 'Ars Amatoria', 'Arte de amar', 'Poema didáctico en dísticos eelgíacos que desea mostrar el arte de dominar al Amor', '179209665', NULL),
(5, 'Remedia Amoris', 'Remedios contra el amor', 'Poema concebido como antídoto del Ars Amatoria, donde Ovidio ofrece consejos y estrategias para poner freno a las desventuras que causa el desamor', '296625461', NULL),
(6, 'Amores', 'Amores', 'Poema en dístico elegíaco siguiendo el modelo de elegía erótica, en el que se recorren todas las situaciones imaginables de la vida amorosoa', '181075384', NULL),
(7, 'Tristia', 'Tristes', 'Cinco libros de poesía elegíaca que recoge un nutrido conjunto de elegías dedicadas a diversos interlocutores animados e inanimados', '174402707', NULL),
(8, 'Epistulae ex Ponto', 'Pónticas', 'Cuatro libros que contienen cuarenta y seis cartas dirigidas a amigos y familiares, aunque algún dios, el lector y el propio emperador se convierten también en dolorosos destinatarios', '176453208', NULL),
(9, 'In Ibin', 'Ibis', 'Poema compuesto en 322 dísticos elegíacos, escrito durante el exilio, en el que sirviéndose de historias míticas maldice y ataca a un individuo que le está perjudicando', '177301298', NULL),
(10, 'Medicamina faciei feminae', 'Sobre la cosmética del rostro femenino', 'Poema didáctico en el que Ovidio defiende el uso de los cosméticos por las mujeres romanas e incluye consejos y recetas para tratamientos ', '213214766', NULL),
(11, 'Halieutica', 'Haliéutica', 'Tratado sobre la pesca de atribución dudosa', '300781386', NULL),
(12, 'Consolatio ad Liviam', 'Consolatio ad Liviam', 'Poema de condolencia dirigido a Livia, la esposa de Augusto, en la muerte de su hijo Druso el Mayor. Atribución dudosa a Ovidio', '219335081', NULL),
(13, 'Nux', 'Nux', 'Poema espurio relacionado con la fábula de Esopo \"El nogal\"', '186736975', NULL),
(14, 'Pulex', 'Pulex', NULL, '', NULL);
